import React, { useState, useEffect, useMemo } from 'react';
import { useParams, Link } from 'react-router-dom';
import './index.css'; // Import the CSS file
import './light.css';
import './dark.css';

function UserSubmissions({ token, setIsLoading }) { // Accept setIsLoading prop
  const { userId } = useParams();
  const [allSubmissions, setAllSubmissions] = useState([]); // Store all fetched submissions
  const [submissions, setSubmissions] = useState([]); // Submissions to display after filtering
  const [error, setError] = useState(null);
  const [filterProblemId, setFilterProblemId] = useState('');
  const [filterStatus, setFilterStatus] = useState('');
  const [filterLanguage, setFilterLanguage] = useState('');
  const [filterStartDate, setFilterStartDate] = useState('');
  const [filterEndDate, setFilterEndDate] = useState('');
  const [sortKey, setSortKey] = useState('submission_id');
  const [sortOrder, setSortOrder] = useState('desc');

  useEffect(() => {
    const fetchSubmissions = async () => {
      setIsLoading(true); // Use global loading
      try {
        const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/users/${userId}/submissions`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setAllSubmissions(data); // Store all submissions
        setSubmissions(data);    // Initially display all submissions
      } catch (error) {
        setError(error);
      } finally {
        setIsLoading(false); // Use global loading
      }
    };

    if (userId && token) {
      fetchSubmissions();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, token]);

  useEffect(() => {
    let filtered = allSubmissions;

    if (filterProblemId) {
      filtered = filtered.filter(submission =>
        submission.problem_id.toLowerCase().includes(filterProblemId.toLowerCase())
      );
    }

    if (filterStatus) {
      filtered = filtered.filter(submission =>
        submission.status.toLowerCase() === filterStatus.toLowerCase()
      );
    }

    if (filterLanguage) {
      filtered = filtered.filter(submission =>
        submission.language.toLowerCase() === filterLanguage.toLowerCase()
      );
    }

    if (filterStartDate) {
      const start = new Date(filterStartDate).getTime();
      filtered = filtered.filter(submission =>
        new Date(submission.timestamp).getTime() >= start
      );
    }

    if (filterEndDate) {
      const end = new Date(filterEndDate).getTime();
      filtered = filtered.filter(submission =>
        new Date(submission.timestamp).getTime() <= end
      );
    }

    setSubmissions(filtered);
  }, [allSubmissions, filterProblemId, filterStatus, filterLanguage, filterStartDate, filterEndDate]);

  const handleSort = (key) => {
    if (sortKey === key) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortKey(key);
      setSortOrder('desc');
    }
  };

  const sortedSubmissions = useMemo(() => {
    return [...submissions].sort((a, b) => {
      if (sortKey === 'submission_id') {
        const aId = parseInt(a.submission_id.substring(1), 10);
        const bId = parseInt(b.submission_id.substring(1), 10);
        if (sortOrder === 'asc') {
          return aId - bId;
        } else {
          return bId - aId;
        }
      } else {
        // Default string comparison for other columns
        const aValue = a[sortKey] ? a[sortKey].toString() : '';
        const bValue = b[sortKey] ? b[sortKey].toString() : '';
        if (sortOrder === 'asc') {
          return aValue.localeCompare(bValue);
        } else {
          return bValue.localeCompare(aValue);
        }
      }
    });
  }, [submissions, sortKey, sortOrder]);

  if (error) {
    return <div className="user-submissions-error">Error: {error.message}</div>;
  }

  if (submissions.length === 0) {
    return <div className="user-submissions-no-submissions">No submissions found for this user.</div>;
  }

  return (
    <div className="user-submissions-container">
      <h2>Submissions for User: {userId} üìã</h2>

      <div className="user-submissions-filters">
        <input
          type="text"
          placeholder="Filter by Problem ID üß©"
          value={filterProblemId}
          onChange={(e) => setFilterProblemId(e.target.value)}
          className="user-submissions-filter-input"
        />
        <select value={filterStatus} onChange={(e) => setFilterStatus(e.target.value)} className="user-submissions-filter-select">
          <option value="">All Statuses üìä</option>
          {[...new Set(allSubmissions.map(s => s.status))].map(status => (
            <option key={status} value={status}>{status}</option>
          ))}
        </select>
        <select value={filterLanguage} onChange={(e) => setFilterLanguage(e.target.value)} className="user-submissions-filter-select">
          <option value="">All Languages üåê</option>
          <option value="c">C</option>
          <option value="cpp">C++</option>
          <option value="python">Python</option>
        </select>
        <input
          type="date"
          placeholder="Start Date üìÖ"
          value={filterStartDate}
          onChange={(e) => setFilterStartDate(e.target.value)}
          className="user-submissions-filter-input"
        />
        <input
          type="date"
          placeholder="End Date üìÖ"
          value={filterEndDate}
          onChange={(e) => setFilterEndDate(e.target.value)}
          className="user-submissions-filter-input"
        />
      </div>

      <table className="user-submissions-table">
        <thead>
          <tr>
            <th onClick={() => handleSort('submission_id')}>
              Submission ID üìÑ {sortKey === 'submission_id' && (sortOrder === 'asc' ? '‚ñ≤' : '‚ñº')}
            </th>
            <th onClick={() => handleSort('problem_id')}>
              Problem ID üß© {sortKey === 'problem_id' && (sortOrder === 'asc' ? '‚ñ≤' : '‚ñº')}
            </th>
            <th onClick={() => handleSort('status')}>
              Status üìä {sortKey === 'status' && (sortOrder === 'asc' ? '‚ñ≤' : '‚ñº')}
            </th>
            <th onClick={() => handleSort('language')}>
              Language üåê {sortKey === 'language' && (sortOrder === 'asc' ? '‚ñ≤' : '‚ñº')}
            </th>
            <th onClick={() => handleSort('timestamp')}>
              Timestamp ‚è∞ {sortKey === 'timestamp' && (sortOrder === 'asc' ? '‚ñ≤' : '‚ñº')}
            </th>
          </tr>
        </thead>
        <tbody>
          {sortedSubmissions.map(submission => (
            <tr key={submission.submission_id}>
              <td>
                <Link to={`/submissions/${submission.submission_id}`}>{submission.submission_id}</Link>
              </td>
              <td>{submission.problem_id}</td>
              <td className={`status-${submission.status.toLowerCase().replace(/ /g, '-').replace(/_/g, '-')}`}>
                {console.log('Submission Status:', submission.status)}
                {submission.status}
              </td>
              <td>{submission.language}</td>
              <td>{new Date(submission.timestamp).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default UserSubmissions;
