import React from 'react';
import Logout from '../Logout';
import { Link } from 'react-router-dom';
import './index.css'; // Import the CSS file
import './light.css';
import './dark.css';

function Header({ isLoggedIn, userName, onLogout, toggleTheme, currentTheme }) {
  return (
    <header>
      <nav className="header-nav">
        <div className="header-logo-container">
          <img src="https://raw.githubusercontent.com/lohitpt252003/DATA/refs/heads/main/data/assets/images/logo.png" alt="Logo" className="header-logo-image" />
          <Link to="/" className="header-link-brand">ANNAFORCES</Link>
        </div>
        <div className="header-nav-links">
          {isLoggedIn && (
            <>
              <Link to="/problems" className="header-nav-link">Problems üß©</Link>
              <Link to="/profile" className="header-nav-link">Profile üë§</Link>
              <Link to="/credits" className="header-nav-link">Credits üèÜ</Link>
            </>
          )}
          <button onClick={toggleTheme} className="theme-toggle-button">
            {currentTheme === 'light' ? 'Dark Mode üåô' : 'Light Mode ‚òÄÔ∏è'}
          </button>
          {isLoggedIn ? (
            <>
              <Logout onLogout={onLogout} />
            </>
          ) : (
            <></>
          )}
        </div>
      </nav>
    </header>
  );
}

export default Header;
