import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import ReactMarkdown from 'react-markdown'; // Import ReactMarkdown
// import { AuthContext } from '../../AuthContext'; // Removed AuthContext import
import './index.css'; // For component-specific styles

const SolutionDetail = ({ setGlobalLoading }) => {
  const { problemId } = useParams();
  // const { token } = useContext(AuthContext); // Removed useContext
  const [solutionData, setSolutionData] = useState(null);
  const [error, setError] = useState(null);
  const [expandedCode, setExpandedCode] = useState({}); // New state for expanded code sections

  useEffect(() => {
    const fetchSolution = async () => {
      const token = localStorage.getItem('token'); // Get token from localStorage
      if (!token) {
        setError('No token found. Please log in.');
        if (setGlobalLoading) setGlobalLoading(false);
        return;
      }

      if (setGlobalLoading) setGlobalLoading(true);
      try {
        const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/problems/${problemId}/solution`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || 'Failed to fetch solution');
        }

        const data = await response.json();
        setSolutionData(data);

        // Initialize all code sections to be expanded by default (optional, can be false)
        const initialExpandedState = {};
        if (data.python) initialExpandedState.python = true;
        if (data.cpp) initialExpandedState.cpp = true;
        if (data.c) initialExpandedState.c = true;
        setExpandedCode(initialExpandedState);

      } catch (err) {
        setError(err.message);
        console.error('Error fetching solution:', err);
      } finally {
        if (setGlobalLoading) setGlobalLoading(false);
      }
    };

    if (problemId) { // Removed token from dependency check here
      fetchSolution();
    }
  }, [problemId, setGlobalLoading]); // Removed token from dependency array

  const toggleCode = (language) => {
    setExpandedCode(prevState => ({
      ...prevState,
      [language]: !prevState[language]
    }));
  };

  if (error) {
    return <div className="solution-detail-error">Error: {error}</div>;
  }

  if (!solutionData) {
    return <div className="solution-detail-loading">Loading solution...</div>;
  }

  return (
    <div className="solution-detail-container">
      <h2 className="solution-detail-title">Solution for Problem {problemId}</h2>

      <div className="solution-detail-section">
        <h3 className="solution-detail-subtitle">Explanation (solution.md)</h3>
        {solutionData.markdown ? (
          <ReactMarkdown>{solutionData.markdown}</ReactMarkdown>
        ) : (
          <p>No markdown explanation available.</p>
        )}
      </div>

      <div className="solution-detail-code-sections">
        <h3 className="solution-detail-subtitle">Code</h3>

        {solutionData.python && (
          <div className="solution-detail-code-block-wrapper">
            <div className="solution-detail-code-header">
              <h4>Python</h4>
              <button onClick={() => toggleCode('python')} className="solution-detail-toggle-button">
                {expandedCode.python ? 'Collapse' : 'Expand'}
              </button>
            </div>
            {expandedCode.python && (
              <pre><code className="language-python">{
                // Remove trailing newline if present, to avoid extra blank line in display
                solutionData.python.endsWith('\n') 
                  ? solutionData.python.slice(0, -1) 
                  : solutionData.python
              }</code></pre>
            )}
          </div>
        )}

        {solutionData.cpp && (
          <div className="solution-detail-code-block-wrapper">
            <div className="solution-detail-code-header">
              <h4>C++</h4>
              <button onClick={() => toggleCode('cpp')} className="solution-detail-toggle-button">
                {expandedCode.cpp ? 'Collapse' : 'Expand'}
              </button>
            </div>
            {expandedCode.cpp && (
              <pre><code className="language-cpp">{
                // Remove trailing newline if present
                solutionData.cpp.endsWith('\n') 
                  ? solutionData.cpp.slice(0, -1) 
                  : solutionData.cpp
              }</code></pre>
            )}
          </div>
        )}

        {solutionData.c && (
          <div className="solution-detail-code-block-wrapper">
            <div className="solution-detail-code-header">
              <h4>C</h4>
              <button onClick={() => toggleCode('c')} className="solution-detail-toggle-button">
                {expandedCode.c ? 'Collapse' : 'Expand'}
              </button>
            </div>
            {expandedCode.c && (
              <pre><code className="language-c">{
                // Remove trailing newline if present
                solutionData.c.endsWith('\n') 
                  ? solutionData.c.slice(0, -1) 
                  : solutionData.c
              }</code></pre>
            )}
          </div>
        )}

        {!solutionData.python && !solutionData.cpp && !solutionData.c && (
          <p>No code solutions available.</p>
        )}
      </div>

      <Link to={`/problems/${problemId}`} className="solution-detail-back-link">Back to Problem</Link>
    </div>
  );
};

export default SolutionDetail;
