import React, { useState, useEffect, useContext } from 'react';
import { useParams, Link } from 'react-router-dom';
// import { AuthContext } from '../../AuthContext';
import './index.css'; // For component-specific styles

const SolutionDetail = ({ setGlobalLoading }) => {
  const { problemId } = useParams();
  const { token } = useContext(AuthContext);
  const [solutionData, setSolutionData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSolution = async () => {
      if (setGlobalLoading) setGlobalLoading(true);
      try {
        const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/problems/${problemId}/solution`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.error || 'Failed to fetch solution');
        }

        const data = await response.json();
        setSolutionData(data);
      } catch (err) {
        setError(err.message);
        console.error('Error fetching solution:', err);
      } finally {
        if (setGlobalLoading) setGlobalLoading(false);
      }
    };

    if (token && problemId) {
      fetchSolution();
    }
  }, [problemId, token, setGlobalLoading]);

  if (error) {
    return <div className="solution-detail-error">Error: {error}</div>;
  }

  if (!solutionData) {
    return <div className="solution-detail-loading">Loading solution...</div>;
  }

  return (
    <div className="solution-detail-container">
      <h2 className="solution-detail-title">Solution for Problem {problemId}</h2>

      <div className="solution-detail-section">
        <h3 className="solution-detail-subtitle">Explanation (solution.md)</h3>
        {solutionData.markdown ? (
          <pre className="solution-detail-markdown-content">{solutionData.markdown}</pre>
        ) : (
          <p>No markdown explanation available.</p>
        )}
      </div>

      <div className="solution-detail-code-sections">
        <h3 className="solution-detail-subtitle">Code</h3>

        {solutionData.python && (
          <div className="solution-detail-code-block">
            <h4>Python</h4>
            <pre><code className="language-python">{
              // Remove trailing newline if present, to avoid extra blank line in display
              solutionData.python.endsWith('\n') 
                ? solutionData.python.slice(0, -1) 
                : solutionData.python
            }</code></pre>
          </div>
        )}

        {solutionData.cpp && (
          <div className="solution-detail-code-block">
            <h4>C++</h4>
            <pre><code className="language-cpp">{
              // Remove trailing newline if present
              solutionData.cpp.endsWith('\n') 
                ? solutionData.cpp.slice(0, -1) 
                : solutionData.cpp
            }</code></pre>
          </div>
        )}

        {solutionData.c && (
          <div className="solution-detail-code-block">
            <h4>C</h4>
            <pre><code className="language-c">{
              // Remove trailing newline if present
              solutionData.c.endsWith('\n') 
                ? solutionData.c.slice(0, -1) 
                : solutionData.c
            }</code></pre>
          </div>
        )}

        {!solutionData.python && !solutionData.cpp && !solutionData.c && (
          <p>No code solutions available.</p>
        )}
      </div>

      <Link to={`/problems/${problemId}`} className="solution-detail-back-link">Back to Problem</Link>
    </div>
  );
};

export default SolutionDetail;
