import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';
import './index.css'; // Import the CSS file
import './light.css';
import './dark.css';

function Profile({ userId, token, setIsLoading }) { // Accept setIsLoading prop
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      setIsLoading(true); // Use global loading
      try {
        const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/users/${userId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();

        setUserData(data);
      } catch (error) {
        setError(error);
      } finally {
        setIsLoading(false); // Use global loading
      }
    };

    if (userId && token) {
      fetchUserData();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId, token]);

  if (error) {
    return <div className="profile-error">Error: {error.message}</div>;
  }

  if (!userData) {
    return <div className="profile-no-data">No user data found.</div>;
  }

  return (
    <div className="profile-container">
      <h2>User Profile üë§</h2>
      <p><strong>User ID:</strong> üÜî {userId}</p>
      <p><strong>Username:</strong> üßë‚Äçüíª {userData.username}</p>
      <p><strong>Name:</strong> üìõ {userData.name}</p>
      <p><strong>Bio:</strong> üìñ {userData.bio}</p>
      <p><strong>Joined:</strong> üìÖ {userData.joined}</p>
      <p><strong>Number of Submissions:</strong> üìã {userData.number_of_submissions}</p>
      <p><Link to={`/users/${userId}/submissions`}>View Submissions üìã</Link></p>
      {/* Add more profile details as needed */}
    </div>
  );
}

export default Profile;
